<template>
    <div class="py-12">
        <div class="grid grid-cols-12 gap-4">
            <TheAside class="col-span-2" />
            <div class="col-span-10 grid grid-cols-3 gap-4">
                <template v-for="item in discoverStore.state.results">
                    <Skeleton active avatar title :paragraph="{ rows: 2 }" size="small"
                        class="border border-gray-200 p-4px rounded w-full" :loading="loading">
                        <figure class="border border-gray-200 mb-0 grid grid-cols-3">
                            <Image class="w-full h-full object-cover"
                                :src="`https://image.tmdb.org/t/p/w300/${item.poster_path}`" alt=""
                                fallback="data:image/png;base64," />
                            <figcaption class="p-2 col-span-2 text-sm grid grid-cols-max-1fr gap-x-2 gap-y-1">

                                <span class="text-black font-medium">Title: </span>
                                <span class="">{{ item.title }}</span>
                                <span class="text-black font-medium">Release: </span>
                                <span>{{ item.release_date }}</span>
                                <span class="text-black font-medium">Genre: </span>
                                <template v-for="genre in item.genre_ids">{{ findGenreNameById(genre) }},</template>
                                <span class="text-black font-medium">Overview: </span>
                                <span>{{ splitText(item.overview) }}</span>
                                <div class="col-span-2 flex justify-end">
                                    <Button type="link" size="small">
                                        <RouterLink :to="{ name: 'SinglePage', params: { id: item.id } }">
                                            More ..
                                        </RouterLink>
                                    </Button>
                                </div>

                            </figcaption>
                        </figure>
                    </Skeleton>
                </template>
            </div>
        </div>
        <Pagination v-model:current="currentPage" simple :total="discoverStore.state.total_pages"
            @change="discoverStore.GetDiscoverMovieHandler(true, currentPage)" />
    </div>
</template>
<script setup lang="ts">
import TheAside from '@/components/TheAside.vue';
import { useDiscoverStore } from '@/stores/DiscoverStore';
import { useGenreStore } from '@/stores/GenreStore';
import { onMounted, ref } from 'vue';
import { Button, Pagination, Skeleton, Image } from "ant-design-vue/es";
const discoverStore = useDiscoverStore()
const genreStore = useGenreStore()
const loading = ref(false)
onMounted(async () => {
    loading.value = true
    await discoverStore.GetDiscoverMovieHandler(true, currentPage.value)
    await genreStore.GetGenreMovieHandler()
    loading.value = false
})
const currentPage = ref(0)
const findGenreNameById = (id: number) => {
    const index = genreStore.state.genres.findIndex((single) => {
        return single.id === id
    })
    return genreStore.state.genres[index]?.name ?? ''
}
const splitText = (text: string) => {
    const textArray = text.split(" ")
    const filterArray = textArray.slice(0, 10)
    return filterArray.join(" ")
}
</script>
